import React from "react";
import ReactDOM from "react-dom";

const Header = props => {
  return <h1>{props.course}</h1>;
};
const Part = props => {
  return (
    <p>
      {props.name} {props.numExercises}
    </p>
  );
};
const Content = props => {
  return (
    <>
      <Part
        //name={props.parts.name[0]}
        numExercises={props.parts.exercises[0]}
      />
      <Part
        name={props.parts.name[1]}
        numExercises={props.parts.exercises[1]}
      />
      <Part
        name={props.parts.name[2]}
        numExercises={props.parts.exercises[2]}
      />
    </>
  );
};
const Total = props => {
  return (
    <p>
      Number of Exercises{" "}
      {props.parts.exercises[0] +
        props.parts.exercises[1] +
        props.parts.exercises[2]}
    </p>
  );
};

const App = () => {
  const course = "Half Stack application development";
  const parts = [
    {
      name: "Fundamentals of React",
      exercises: 10
    },
    {
      name: "Using props to pass data",
      exercises: 7
    },
    {
      name: "State of a component",
      exercises: 14
    }
  ];
  return (
    <div>
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));
