import React from "react";
import ReactDOM from "react-dom";

const Header = props => {
  return <h1>{props.course}</h1>;
};
const Part = props => {
  return (
    <p>
      {props.name} {props.numExercises}
    </p>
  );
};
const Content = props => {
  return (
    <>
      <Part name={props.name1} numExercises={props.ex1} />
      <Part name={props.name2} numExercises={props.ex2} />
      <Part name={props.name3} numExercises={props.ex3} />
    </>
  );
};
const Total = props => {
  return <p>Number of Exercises {props.exerciseNums}</p>;
};

const App = () => {
  const course = "Half Stack application development";
  const parts = [
    {
      name: "Fundamentals of React",
      exercises: 10
    },
    {
      name: "Using props to pass data",
      exercises: 7
    },
    {
      name: "State of a component",
      exercises: 14
    }
  ];
  return (
    <div>
      <Header course={course} />
      <Content
        name1={parts[0].name}
        name2={parts[1].name}
        name3={parts[2].name}
        ex1={parts[0].exercises}
        ex2={parts[1].exercises}
        ex3={parts[2].exercises}
      />
      <Total
        exerciseNums={
          parts[0].exercises + parts[1].exercises + parts[2].exercises
        }
      />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));
