import React from "react";
import ReactDOM from "react-dom";

const Header = props => {
  return <h1>{props.course}</h1>;
};
const Part = props => {
  return (
    <p>
      {props.name} {props.numExercises}
    </p>
  );
};
const Content = props => {
  return (
    <>
      <Part name={props.name1} numExercises={props.ex1} />
      <Part name={props.name2} numExercises={props.ex2} />
      <Part name={props.name3} numExercises={props.ex3} />
    </>
  );
};
const Total = props => {
  return <p>Number of Exercises {props.exerciseNums}</p>;
};

const App = () => {
  const course = "Half Stack application development";
  const part1 = {
    name: "Fundamentals of React",
    exercises: 10
  };
  const part2 = {
    name: "Using props to pass data",
    exercises: 7
  };
  const part3 = {
    name: "State of a component",
    exercises: 14
  };
  return (
    <div>
      <Header course={course} />
      <Content
        name1={part1.name}
        name2={part2.name}
        name3={part3.name}
        ex1={part1.exercises}
        ex2={part2.exercises}
        ex3={part3.exercises}
      />
      <Total
        exerciseNums={part1.exercises + part2.exercises + part3.exercises}
      />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));
